mapper.select:
select company_code as companyCode, item_code as itemCode,item_name as itemName,item_no as itemNo,item_type,item_specification as itemSpecification,item_stock_unit as itemStockUnit,lotsize,memo,useyn,createdate,updatedate from ma_item
where item_category='반제품' AND (useyn='Y' or useyn is null) AND company_code=#{companyCode}
order by item_code

mapper.update:
update ma_item
set item_name=#{elements.itemName}, item_no=#{elements.itemNo}, item_type=#{elements.item_type} ,item_specification=#{elements.itemSpecification},item_stock_unit=#{elements.itemStockUnit}, lotsize=#{elements.lotSize}, memo=#{elements.memo},useyn=#{elements.useyn} ,updatedate=CURRENT_TIMESTAMP
where item_code=#{elements.itemCode}


update ma_item
set useyn='N'
where item_code=#{elements.itemCode}
